sap.ui.define(
  [
    "sap/ui/model/json/JSONModel",
    "sap/ui/Device"
  ],
  function (JSONModel, Device) {
    "use strict";

    return {
      formatImage: function (base64) {
        //aggiungere base64 come parametro in input
        //if ( !base64) return "../public/img/logo.jpg"

        base64 =
          "";
        return `data:image/jpeg;base64,${base64}`;
      },

      calculateHeight: function (iHeight) {
        return iHeight - 200 + "px";
      },

      formatDate(jsDate) {
        if( !jsDate ) return;

        return jsDate.toLocaleDateString()
      }
    };
  }
);
